mapper.update:
update chat_message
set is_read = 1
where session_id = #{sessionId}
  and receiver_id = #{receiverId}
  and is_read = 0

mapper.select:
select count(*) from chat_message
where session_id = #{sessionId}
  and receiver_id = #{receiverId}
  and is_read = 0

select count(*) from chat_message
where receiver_id = #{receiverId}
  and is_read = 0

select
,
s.name as sender_name, s.img as sender_img,
r.name as receiver_name, r.img as receiver_img
from chat_message cm
left join user s on cm.sender_id = s.id
left join user r on cm.receiver_id = r.id


    and cm.session_id = #{sessionId}


    and cm.sender_id = #{senderId}


    and cm.receiver_id = #{receiverId}


    and cm.message_type = #{messageType}


order by cm.create_time desc

  limit #{page}, #{limit}

select count(*) from chat_message cm


    and cm.session_id = #{sessionId}


    and cm.sender_id = #{senderId}


    and cm.receiver_id = #{receiverId}


    and cm.message_type = #{messageType}

mapper.update:
update chat_message
set is_revoked = 1
where id = #{id}
  and sender_id = #{senderId}
  and (is_revoked = 0 OR is_revoked IS NULL)
