mapper.update:
update chat_session
set status = #{status}
where id = #{id}

update chat_session
set last_message_time = NOW()
where id = #{id}

mapper.select:
select
,
u.name as user_name, u.img as user_img,
d.name as doctor_name, d.img as doctor_img
from chat_session cs
left join user u on cs.user_id = u.id
left join user d on cs.doctor_id = d.id


    and cs.user_id = #{userId}


    and cs.doctor_id = #{doctorId}


    and cs.status = #{status}


order by cs.last_message_time desc, cs.create_time desc

  limit #{page}, #{limit}

select count(*) from chat_session cs


    and cs.user_id = #{userId}


    and cs.doctor_id = #{doctorId}


    and cs.status = #{status}
