mapper.select:
select

from doctor_schedule
where doctor_id = #{doctorId,jdbcType=BIGINT}
  and week_day = #{weekDay,jdbcType=INTEGER}
order by time_slot asc

select

from doctor_schedule
where doctor_id = #{doctorId,jdbcType=BIGINT}
  and week_day = #{weekDay,jdbcType=INTEGER}
  and time_slot = #{timeSlot,jdbcType=VARCHAR}
limit 1

select

from doctor_schedule
where doctor_id = #{doctorId,jdbcType=BIGINT}
order by week_day asc, time_slot asc

    limit #{page,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

select count(*)
from doctor_schedule
where doctor_id = #{doctorId,jdbcType=BIGINT}
