mapper.select:
SELECT
mr.id, mr.pet_id, mr.user_id, mr.doctor_id, mr.medicine_id, 
mr.quantity, mr.dosage, mr.`usage`, mr.create_time, mr.diagnosis_id
FROM `medicine_record` mr


    AND mr.user_id = #{userId}


    AND mr.doctor_id = #{doctorId}


    AND mr.pet_id = #{petId}


    AND mr.diagnosis_id = #{diagnosisId}


ORDER BY mr.id DESC

  limit #{page}, #{limit}

SELECT count(mr.id)
FROM `medicine_record` mr


    AND mr.user_id = #{userId}


    AND mr.doctor_id = #{doctorId}


    AND mr.pet_id = #{petId}


    AND mr.diagnosis_id = #{diagnosisId}
